const toBinary = str => {
    var outStr = '';
    for (var c in str) {
        var temp = str.charCodeAt(c).toString(2);
        while (temp.length<8) {temp = '0'+temp}
        // console.log(temp)
        outStr += temp
    }
    return outStr
}

const fromBinary = str => {
    var chars = str.match(/(.){8}/g);
    return chars.map(c => String.fromCharCode(parseInt(c, 2))).join('');
}

